import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/Profile.css";

const Profile = () => {
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState(null);

  const getProfile = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:5000/api/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setProfile(response.data);
    } catch (err) {
      setError("Profile not found or error fetching profile.");
    }
  };

  useEffect(() => {
    getProfile();
  }, []);

  if (!profile) {
    return <h2>Loading profile...</h2>;
  }

  return (
    <div className="profile-container">
      <h2>MY PROFILE</h2>
      <h3>{profile.username || "User"}</h3>
      <h4>Email: {profile.email || "N/A"}</h4>

      <hr />
      <p><b>Bio:</b> {profile.bio || "No bio set"}</p>

      <div className="grid-container">
        <div className="grid-item"><b>Instruments:</b> {profile.instruments?.join(", ") || "None"}</div>
        <div className="grid-item"><b>Genres:</b> {profile.genres?.join(", ") || "None"}</div>
        <div className="grid-item"><b>Years of Experience:</b> {profile.yearsOfExperience || "0"}</div>
        <div className="grid-item"><b>Influences:</b> {profile.influences?.join(", ") || "None"}</div>
        <div className="grid-item"><b>City:</b> {profile.city || "N/A"}</div>
        <div className="grid-item"><b>Social Media:</b></div>
        <div className="grid-item">
          {profile.socialMediaLinks?.map((link, index) => (
            <a key={index} href={link} target="_blank" rel="noopener noreferrer">{link}</a>
          )) || "No social media added"}
        </div>
      </div>

      <button onClick={() => navigate("/edit-profile")}>Edit My Profile</button>
    </div>
  );
};

export default Profile;
