import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ProfileEdit = () => {
  const navigate = useNavigate();

  const [profile, setProfile] = useState({
    email: "",
    instruments: "",
    genres: "",
    yearsOfExperience: 0,
    influences: "",
    city: "",
    bio: "",
    socialMediaLinks: {
      youtube: "",
      instagram: "",
    },
  });

  // Fetch profile details when the component loads
  useEffect(() => {
    getProfile();
  }, []);

  const getProfile = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:5000/api/profile", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      // Set profile data into the state
      setProfile({
        email: response.data.email || "",
        instruments: response.data.instruments?.join(", ") || "",
        genres: response.data.genres?.join(", ") || "",
        yearsOfExperience: response.data.yearsOfExperience || 0,
        influences: response.data.influences?.join(", ") || "",
        city: response.data.city || "",
        bio: response.data.bio || "",
        socialMediaLinks: {
          youtube: response.data.socialMediaLinks?.youtube || "",
          instagram: response.data.socialMediaLinks?.instagram || "",
        },
      });
    } catch (error) {
      console.error("Error fetching profile:", error);
    }
  };

  // Handle input changes dynamically
  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile((prevProfile) => ({
      ...prevProfile,
      [name]: value,
    }));
  };

  const handleSocialChange = (e) => {
    const { name, value } = e.target;
    setProfile((prevProfile) => ({
      ...prevProfile,
      socialMediaLinks: {
        ...prevProfile.socialMediaLinks,
        [name]: value,
      },
    }));
  };

  // Update profile
  const handleUpdate = async () => {
    try {
      const token = localStorage.getItem("token");

      const response = await axios.post(
        "http://localhost:5000/api/profile",
        {
          instruments: profile.instruments.split(", "),
          genres: profile.genres.split(", "),
          yearsOfExperience: profile.yearsOfExperience,
          influences: profile.influences.split(", "),
          city: profile.city,
          bio: profile.bio,
          socialMediaLinks: {
            youtube: profile.socialMediaLinks.youtube,
            instagram: profile.socialMediaLinks.instagram,
          },
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.data) {
        alert("Profile updated successfully!");
        navigate("/profile");
      }
    } catch (error) {
      console.error("Error updating profile:", error);
      alert("Something went wrong while updating your profile.");
    }
  };

  return (
    <div className="profile-edit-container">
      <h2 className="profile-title">EDIT PROFILE</h2>
      <h3>{profile.email}</h3>

      <div className="input-group">
        <label>Instruments</label>
        <input
          type="text"
          name="instruments"
          value={profile.instruments}
          onChange={handleChange}
          placeholder="Enter instruments (comma separated)"
        />
      </div>

      <div className="input-group">
        <label>Genres</label>
        <input
          type="text"
          name="genres"
          value={profile.genres}
          onChange={handleChange}
          placeholder="Enter genres (comma separated)"
        />
      </div>

      <div className="input-group">
        <label>Years of Experience</label>
        <input
          type="number"
          name="yearsOfExperience"
          value={profile.yearsOfExperience}
          onChange={handleChange}
        />
      </div>

      <div className="input-group">
        <label>Influences</label>
        <input
          type="text"
          name="influences"
          value={profile.influences}
          onChange={handleChange}
          placeholder="Enter influences (comma separated)"
        />
      </div>

      <div className="input-group">
        <label>City</label>
        <input
          type="text"
          name="city"
          value={profile.city}
          onChange={handleChange}
          placeholder="Enter city"
        />
      </div>

      <div className="input-group">
        <label>Bio</label>
        <textarea
          name="bio"
          value={profile.bio}
          onChange={handleChange}
          placeholder="Tell us about yourself"
        ></textarea>
      </div>

      <div className="input-group">
        <label>Youtube Link</label>
        <input
          type="text"
          name="youtube"
          value={profile.socialMediaLinks.youtube}
          onChange={handleSocialChange}
          placeholder="Enter Youtube link"
        />
      </div>

      <div className="input-group">
        <label>Instagram Link</label>
        <input
          type="text"
          name="instagram"
          value={profile.socialMediaLinks.instagram}
          onChange={handleSocialChange}
          placeholder="Enter Instagram link"
        />
      </div>

      <div className="buttons">
        <button className="btn-back" onClick={() => navigate("/profile")}>
          Back
        </button>
        <button className="btn-update" onClick={handleUpdate}>
          Update My Profile
        </button>
      </div>
    </div>
  );
};

export default ProfileEdit;
