import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar"; // Import Navbar
import "../styles/Profile.css";

const Profile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState({});
  const [profile, setProfile] = useState(null);

  const getProfile = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:5000/api/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setUser(response.data.user);
      setProfile(response.data.profile);
    } catch (error) {
      console.error("Error fetching profile:", error);
    }
  };

  useEffect(() => {
    getProfile();
  }, []);

  return (
    <div className="profile-container">
      <Navbar />
      <h2 className="profile-title">MY PROFILE</h2>
      <h3 className="username">{user?.username || "User"}</h3>
      <p className="email">Email: {user?.email || "N/A"}</p>

      <hr className="divider" />

      {profile ? (
        <>
          <div className="bio-section">
            <h4>Bio:</h4>
            <p>{profile.bio || "No bio available"}</p>
          </div>

          <div className="grid-container">
            <div className="grid-item">
              <b>Instruments:</b> {profile.instruments?.join(", ") || "None"}
            </div>
            <div className="grid-item">
              <b>Genres:</b> {profile.genres?.join(", ") || "None"}
            </div>
            <div className="grid-item">
              <b>Years of Experience:</b> {profile.yearsOfExperience || "N/A"}
            </div>
            <div className="grid-item">
              <b>Influences:</b> {profile.influences?.join(", ") || "None"}
            </div>
            <div className="grid-item">
              <b>City:</b> {profile.city || "N/A"}
            </div>
            <div className="grid-item">
              <b>Social Media:</b>
              {profile.socialLinks?.length
                ? profile.socialLinks.map((link, i) => (
                    <div key={i}>
                      <a href={link} target="_blank" rel="noopener noreferrer">
                        {link}
                      </a>
                    </div>
                  ))
                : "None"}
            </div>
          </div>
        </>
      ) : (
        <p>No profile found. Please update your profile.</p>
      )}

      <button className="edit-btn" onClick={() => navigate("/edit-profile")}>
        Edit My Profile
      </button>
    </div>
  );
};

export default Profile;
