import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ProfileEdit = () => {
    const navigate = useNavigate();

    const [profile, setProfile] = useState({
        instruments: "",
        genres: "",
        influences: "",
        city: "",
        youtubeLink: "",
        instaLink: "",
        bio: "",
        yearsOfExperience: "",
    });

    const getProfile = async () => {
        try {
            const token = localStorage.getItem("token");
            const response = await axios.get("http://localhost:5000/api/profile", {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (response.data) {
                console.log("Fetched Profile:", response.data);
                setProfile({
                    instruments: response.data.instruments.join(", "),
                    genres: response.data.genres.join(", "),
                    influences: response.data.influences.join(", "),
                    city: response.data.city || "",
                    youtubeLink: response.data.socialLinks?.youtube || "",
                    instaLink: response.data.socialLinks?.instagram || "",
                    bio: response.data.bio || "",
                    yearsOfExperience: response.data.yearsOfExperience || "", 
                });
            }
        } catch (error) {
            console.error("Error fetching profile:", error);
        }
    };

    const handleUpdate = async () => {
        try {
            const token = localStorage.getItem("token");

            const instrumentsArray = profile.instruments ? profile.instruments.split(",") : [];
            const genresArray = profile.genres ? profile.genres.split(",") : [];
            const influencesArray = profile.influences ? profile.influences.split(",") : [];

            const response = await axios.put("http://localhost:5000/api/profile", {
                instruments: instrumentsArray,
                genres: genresArray,
                influences: influencesArray,
                city: profile.city,
                socialLinks: {
                    youtube: profile.youtubeLink,
                    instagram: profile.instaLink,
                },
                bio: profile.bio,
                yearsOfExperience: Number(profile.yearsOfExperience), 
            }, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (response.data) {
                alert("Profile updated successfully!");
                navigate("/profile");
            }
        } catch (error) {
            alert("Something went wrong while updating your profile.");
            console.error("Update Error:", error);
        }

        console.log("Sending update:", {
            instruments: profile.instruments.split(",").map(item => item.trim()),
            genres: profile.genres.split(",").map(item => item.trim()),
            influences: profile.influences.split(",").map(item => item.trim()),
            city: profile.city,
            socialLinks: {
                youtube: profile.youtubeLink,
                instagram: profile.instaLink,
            },
            bio: profile.bio,
            yearsOfExperience: Number(profile.yearsOfExperience),
        });

    };

    useEffect(() => {
        getProfile();
    }, []);

    return (
        <div className="form-container">
            <h2 className="form-title">EDIT PROFILE</h2>

            <div className="input-group">
                <label>Instruments</label>
                <input
                    type="text"
                    placeholder="Enter instruments (comma separated)"
                    value={profile.instruments}
                    onChange={(e) => setProfile({ ...profile, instruments: e.target.value })}
                />
            </div>

            <div className="input-group">
                <label>Genres</label>
                <input
                    type="text"
                    placeholder="Enter genres (comma separated)"
                    value={profile.genres}
                    onChange={(e) => setProfile({ ...profile, genres: e.target.value })}
                />
            </div>

            <div className="input-group">
                <label>Years of Experience</label>
                <input
                    type="number"
                    placeholder="Years of Experience"
                    value={profile.yearsOfExperience}
                    onChange={(e) => setProfile({ ...profile, yearsOfExperience: e.target.value })}
                />
            </div>

            <div className="input-group">
                <label>Influences</label>
                <input
                    type="text"
                    placeholder="Enter influences (comma separated)"
                    value={profile.influences}
                    onChange={(e) => setProfile({ ...profile, influences: e.target.value })}
                />
            </div>

            <div className="input-group">
                <label>City</label>
                <input
                    type="text"
                    placeholder="Enter city"
                    value={profile.city}
                    onChange={(e) => setProfile({ ...profile, city: e.target.value })}
                />
            </div>

            <div className="input-group">
                <label>Bio</label>
                <textarea
                    rows="4"
                    placeholder="Tell us about yourself"
                    value={profile.bio}
                    onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
                />
            </div>

            <div className="input-group">
                <label>Youtube Link</label>
                <input
                    type="text"
                    placeholder="Enter Youtube link"
                    value={profile.youtubeLink}
                    onChange={(e) => setProfile({ ...profile, youtubeLink: e.target.value })}
                />
            </div>

            <div className="input-group">
                <label>Instagram Link</label>
                <input
                    type="text"
                    placeholder="Enter Instagram link"
                    value={profile.instaLink}
                    onChange={(e) => setProfile({ ...profile, instaLink: e.target.value })}
                />
            </div>

            <button className="form-btn" onClick={handleUpdate}>
                Update My Profile
            </button>
        </div>
    );
};

export default ProfileEdit;
