import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/Profile.css";

const Profile = () => {
    const navigate = useNavigate();
    const [profile, setProfile] = useState({});
    const [errorStatus, setErrorStatus] = useState(null);
    const [warning, setWarning] = useState(null);

    useEffect(() => {
        const getProfile = async () => {
            try {
                const token = localStorage.getItem("token");
                const response = await axios.get("http://localhost:5000/api/profile", {
                    headers: { Authorization: `Bearer ${token}` }
                });

                setProfile(response.data);
            } catch (error) {
                setErrorStatus(error.response?.status);
            }
        };

        getProfile();

        if (errorStatus === 404) {
            setWarning("Your music profile is blank! Update it now.");
        } else if (errorStatus) {
            setWarning("Something went wrong. Try updating your profile.");
        }
    }, [errorStatus]);

    return (
        <div className="profile-container">
            <h2 className="profile-title">MY PROFILE</h2>
            <h3 className="username">{profile.username || "Username"}</h3>
            <h4 className="email">Email: {profile.email || "user@example.com"}</h4>

            {warning && <p className="warning-message">{warning}</p>}

            <div className="profile-details">
                <p><strong>Instruments:</strong> {profile.instruments?.join(", ") || "Not set"}</p>
                <p><strong>Genres:</strong> {profile.genres?.join(", ") || "Not set"}</p>
                <p><strong>Years of Experience:</strong> {profile.experience || "Not set"}</p>
                <p><strong>Influences:</strong> {profile.influences?.join(", ") || "Not set"}</p>
                <p><strong>City:</strong> {profile.city || "Not set"}</p>
                <p><strong>Social Media:</strong> 
                    {profile.socialLinks?.instagram ? 
                        <a href={profile.socialLinks.instagram}> Instagram</a> : " Not set"}
                </p>
                <p><strong>Bio:</strong> {profile.bio || "Not set"}</p>
            </div>

            <button className="profile-btn" onClick={() => navigate("/edit-profile")}>
                Edit My Profile
            </button>
        </div>
    );
};

export default Profile;
