import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/ProfileEdit.css";

const ProfileEdit = () => {
    const navigate = useNavigate();

    const handleNavigation = (path) => {
        navigate(path);
    };

    const [instruments, setInstruments] = useState("");
    const [genres, setGenres] = useState("");
    const [influences, setInfluences] = useState("");
    const [city, setCity] = useState("");
    const [youtubeLink, setYoutubeLink] = useState("");
    const [instaLink, setInstagramLink] = useState("");
    const [bio, setBio] = useState("");
    const [error, setError] = useState("");

    const getProfile = async () => {
        try {
            const token = localStorage.getItem("token");
            const response = await axios.get("http://localhost:5000/api/profile", {
                headers: { Authorization: `Bearer ${token}` },
            });

            if (response && response.data) {
                setInstruments(response.data.instruments?.join(",") || "");
                setGenres(response.data.genres?.join(",") || "");
                setInfluences(response.data.influences?.join(",") || "");
                setCity(response.data.city || "");
                setYoutubeLink(response.data.socialLinks?.youtube || "");
                setInstagramLink(response.data.socialLinks?.instagram || "");
                setBio(response.data.bio || "");
            }
        } catch (error) {
            console.error("Error fetching profile:", error);

            if (error.response) {
                setError(`Error: ${error.response.status} - ${error.response.data.msg}`);
            } else {
                setError("Failed to fetch profile. Please check your internet connection.");
            }
        }
    };

    const handleUpdate = async () => {
        try {
            const token = localStorage.getItem("token");

            const instrumentsArray = instruments.split(",");
            const genresArray = genres.split(",");
            const influencesArray = influences.split(",");

            const response = await axios.post(
                "http://localhost:5000/api/profile",
                {
                    instruments: instrumentsArray,
                    genres: genresArray,
                    influences: influencesArray,
                    city,
                    socialLinks: {
                        youtube: youtubeLink,
                        instagram: instaLink,
                    },
                    bio,
                },
                {
                    headers: { Authorization: `Bearer ${token}` },
                }
            );

            if (response.data) {
                alert("Profile updated successfully!");
                navigate("/profile");
            }
        } catch (error) {
            alert("Error updating profile. Please try again.");
            console.error("Profile update error:", error);
        }
    };

    useEffect(() => {
        getProfile();
    }, []);

    return (
        <div className="profile-edit-container">
            <div className="profile-edit-card">
                <h2 className="profile-title">EDIT PROFILE</h2>
                <h3 className="profile-username">[User Name]</h3>
                <h4 className="profile-email">email: [user email]</h4>

                {error && <p className="error-message">{error}</p>}

                <div className="form-group">
                    <label>Bio</label>
                    <textarea rows="4" value={bio} onChange={(e) => setBio(e.target.value)}></textarea>
                </div>

                <div className="form-group">
                    <label>Instruments</label>
                    <input type="text" placeholder="e.g. Guitar, Vocals" value={instruments} onChange={(e) => setInstruments(e.target.value)} />
                </div>

                <div className="form-group">
                    <label>Genres</label>
                    <input type="text" placeholder="e.g. Rock, Blues" value={genres} onChange={(e) => setGenres(e.target.value)} />
                </div>

                <div className="form-group">
                    <label>Years of Experience</label>
                    <input type="number" placeholder="Years of Experience" />
                </div>

                <div className="form-group">
                    <label>Influences</label>
                    <input type="text" placeholder="e.g. Jimi Hendrix, Eric Clapton" value={influences} onChange={(e) => setInfluences(e.target.value)} />
                </div>

                <div className="form-group">
                    <label>City</label>
                    <input type="text" placeholder="Enter your city" value={city} onChange={(e) => setCity(e.target.value)} />
                </div>

                <div className="form-group">
                    <label>Social Media Links</label>
                    <input type="text" placeholder="Youtube link" value={youtubeLink} onChange={(e) => setYoutubeLink(e.target.value)} />
                    <input type="text" placeholder="Instagram link" value={instaLink} onChange={(e) => setInstagramLink(e.target.value)} />
                </div>

                <button className="profile-btn back-btn" onClick={() => handleNavigation("/profile")}>
                    Back
                </button>
                <button className="profile-btn update-btn" onClick={handleUpdate}>
                    Update My Profile
                </button>
            </div>
        </div>
    );
};

export default ProfileEdit;
