import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ProfileEdit = () => {
  const navigate = useNavigate();

  const [profile, setProfile] = useState({
    instruments: "",
    genres: "",
    yearsOfExperience: 0,
    influences: "",
    city: "",
    socialLinks: { youtube: "", instagram: "" },
    bio: "",
    email: "",
  });

  const getProfile = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:5000/api/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log("Fetched Profile:", response.data);

      setProfile({
        instruments: response.data.instruments.join(", "),
        genres: response.data.genres.join(", "),
        yearsOfExperience: response.data.yearsOfExperience,
        influences: response.data.influences.join(", "),
        city: response.data.city,
        bio: response.data.bio,
        socialLinks: response.data.socialLinks || { youtube: "", instagram: "" },
        email: response.data.email || "",
      });
    } catch (error) {
      console.error("Error fetching profile:", error);
    }
  };

  const handleUpdate = async () => {
    try {
      const token = localStorage.getItem("token");

      const updatedProfile = {
        instruments: profile.instruments ? profile.instruments.split(",").map((i) => i.trim()) : [],
        genres: profile.genres ? profile.genres.split(",").map((g) => g.trim()) : [],
        yearsOfExperience: profile.yearsOfExperience ? Number(profile.yearsOfExperience) : 0,
        influences: profile.influences ? profile.influences.split(",").map((i) => i.trim()) : [],
        city: profile.city || "",
        bio: profile.bio || "",
        socialLinks: {
          youtube: profile.socialLinks?.youtube || "",
          instagram: profile.socialLinks?.instagram || "",
        },
      };

      console.log("Sending Update:", updatedProfile);

      const response = await axios.patch("http://localhost:5000/api/profile", updatedProfile, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.data) {
        alert("Profile updated successfully!");
        navigate("/profile");
      }
    } catch (error) {
      console.error("Update error:", error.response ? error.response.data : error.message);
      alert("Something went wrong while updating your profile.");
    }
  };



  useEffect(() => {
    getProfile();
  }, []);

  return (
    <div className="form-container">
      <h2 className="form-title">EDIT PROFILE</h2>
      <h3>{profile.email}</h3>
      <form>
        <div className="input-group">
          <label>Instruments</label>
          <input
            type="text"
            value={profile.instruments}
            onChange={(e) => setProfile({ ...profile, instruments: e.target.value })}
          />
        </div>

        <div className="input-group">
          <label>Genres</label>
          <input
            type="text"
            value={profile.genres}
            onChange={(e) => setProfile({ ...profile, genres: e.target.value })}
          />
        </div>

        <div className="input-group">
          <label>Years of Experience</label>
          <input
            type="number"
            value={profile.yearsOfExperience}
            onChange={(e) => setProfile({ ...profile, yearsOfExperience: e.target.value })}
          />
        </div>

        <div className="input-group">
          <label>Influences</label>
          <input
            type="text"
            value={profile.influences}
            onChange={(e) => setProfile({ ...profile, influences: e.target.value })}
          />
        </div>

        <div className="input-group">
          <label>City</label>
          <input
            type="text"
            value={profile.city}
            onChange={(e) => setProfile({ ...profile, city: e.target.value })}
          />
        </div>

        <div className="input-group">
          <label>Bio</label>
          <textarea
            value={profile.bio}
            onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
          />
        </div>

        <div className="input-group">
          <label>Youtube Link</label>
          <input
            type="text"
            value={profile.socialLinks.youtube}
            onChange={(e) => setProfile({
              ...profile,
              socialLinks: { ...profile.socialLinks, youtube: e.target.value },
            })}
          />
        </div>

        <div className="input-group">
          <label>Instagram Link</label>
          <input
            type="text"
            value={profile.socialLinks.instagram}
            onChange={(e) => setProfile({
              ...profile,
              socialLinks: { ...profile.socialLinks, instagram: e.target.value },
            })}
          />
        </div>

        <button type="button" className="form-btn" onClick={handleUpdate}>
          Update My Profile
        </button>
      </form>
    </div>
  );
};

export default ProfileEdit;
