import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/Profile.css";

const Profile = () => {
  const navigate = useNavigate();
  const [profile, setProfile] = useState({});
  const [user, setUser] = useState({ username: "User", email: "N/A" });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const getProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        const userResponse = await axios.get("http://localhost:5000/api/auth/user", {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (userResponse.data) {
          setUser(userResponse.data);
        }

        const profileResponse = await axios.get("http://localhost:5000/api/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (profileResponse.data) {
          setProfile(profileResponse.data);
        }
      } catch (error) {
        console.error("Error fetching profile:", error);
      } finally {
        setLoading(false);
      }
    };

    getProfile();
  }, []);

  if (loading) return <p>Loading...</p>;

  return (
    <div className="profile-container">
      <div className="profile-card">
        <h2 className="profile-title">MY PROFILE</h2>
        <h3 className="profile-username">{user.username}</h3>
        <p className="profile-email">Email: {user.email}</p>
        <hr />
        <p><strong>Bio:</strong> {profile.bio || "N/A"}</p>
        <div className="profile-grid">
          <p><strong>Instruments:</strong> {profile.instruments?.join(", ") || "None"}</p>
          <p><strong>Genres:</strong> {profile.genres?.join(", ") || "None"}</p>
          <p><strong>Years of Experience:</strong> {profile.yearsOfExperience || "N/A"}</p>
          <p><strong>Influences:</strong> {profile.influences?.join(", ") || "None"}</p>
          <p><strong>City:</strong> {profile.city || "N/A"}</p>
          <p><strong>Social Media:</strong> {profile.socialMediaLinks?.join(", ") || "None"}</p>
        </div>
        <button className="edit-profile-btn" onClick={() => navigate("/edit-profile")}>
          Edit My Profile
        </button>
      </div>
    </div>
  );
};

export default Profile;
