import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/ProfileEdit.css"; // Ensure this file exists and is linked

const ProfileEdit = () => {
    const navigate = useNavigate();

    // State variables for profile fields
    const [instruments, setInstruments] = useState("");
    const [genres, setGenres] = useState("");
    const [influences, setInfluences] = useState("");
    const [city, setCity] = useState("");
    const [yearsOfExperience, setYearsOfExperience] = useState("");
    const [youtubeLink, setYoutubeLink] = useState("");
    const [instaLink, setInstagramLink] = useState("");
    const [bio, setBio] = useState("");
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    // Function to fetch user profile
    const getProfile = async () => {
        try {
            const token = localStorage.getItem("token");
            const response = await axios.get("http://localhost:5000/api/profile", {
                headers: { Authorization: `Bearer ${token}` },
            });

            if (response.data) {
                setInstruments(response.data.instruments?.join(",") || "");
                setGenres(response.data.genres?.join(",") || "");
                setInfluences(response.data.influences?.join(",") || "");
                setCity(response.data.city || "");
                setYearsOfExperience(response.data.yearsOfExperience || "");
                setYoutubeLink(response.data.socialLinks?.youtube || "");
                setInstagramLink(response.data.socialLinks?.instagram || "");
                setBio(response.data.bio || "");
            }
        } catch (error) {
            console.error("Error fetching profile:", error);
            setError("Failed to load profile. Please try again.");
        }
    };

    // Fetch profile on component mount
    useEffect(() => {
        getProfile();
    }, []);

    // Function to update profile
    const handleUpdate = async () => {
        setLoading(true);
        setError("");

        try {
            const token = localStorage.getItem("token");

            // Ensure yearsOfExperience is stored as a number
            const formattedYearsOfExperience = Number(yearsOfExperience) || 0;

            const response = await axios.post(
                "http://localhost:5000/api/profile",
                {
                    instruments: instruments.split(","),
                    genres: genres.split(","),
                    influences: influences.split(","),
                    city,
                    yearsOfExperience: formattedYearsOfExperience,
                    socialLinks: {
                        youtube: youtubeLink,
                        instagram: instaLink,
                    },
                    bio,
                },
                {
                    headers: { Authorization: `Bearer ${token}` },
                }
            );

            if (response.data) {
                alert("Profile updated successfully!");
                getProfile(); // Fetch updated data immediately
                navigate("/profile");
            }
        } catch (error) {
            setError("Something went wrong with profile update. Please try again.");
            console.error("Profile update error:", error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="profile-edit-container">
            <div className="profile-edit-box">
                <h2 className="profile-title">EDIT PROFILE</h2>
                {error && <p className="error-message">{error}</p>}

                <div className="profile-input-group">
                    <label>Bio</label>
                    <textarea
                        rows="4"
                        value={bio}
                        onChange={(e) => setBio(e.target.value)}
                    ></textarea>
                </div>

                <div className="profile-input-group">
                    <label>Instruments</label>
                    <input
                        type="text"
                        placeholder="Instruments (comma separated)"
                        value={instruments}
                        onChange={(e) => setInstruments(e.target.value)}
                    />
                </div>

                <div className="profile-input-group">
                    <label>Genres</label>
                    <input
                        type="text"
                        placeholder="Genres (comma separated)"
                        value={genres}
                        onChange={(e) => setGenres(e.target.value)}
                    />
                </div>

                <div className="profile-input-group">
                    <label>Years of Experience</label>
                    <input
                        type="number"
                        placeholder="Years of Experience"
                        value={yearsOfExperience}
                        onChange={(e) => setYearsOfExperience(e.target.value)}
                    />
                </div>

                <div className="profile-input-group">
                    <label>Influences</label>
                    <input
                        type="text"
                        placeholder="Influences (comma separated)"
                        value={influences}
                        onChange={(e) => setInfluences(e.target.value)}
                    />
                </div>

                <div className="profile-input-group">
                    <label>City</label>
                    <input
                        type="text"
                        placeholder="City"
                        value={city}
                        onChange={(e) => setCity(e.target.value)}
                    />
                </div>

                <div className="profile-input-group">
                    <label>Social Media Links</label>
                    <input
                        type="text"
                        placeholder="YouTube link"
                        value={youtubeLink}
                        onChange={(e) => setYoutubeLink(e.target.value)}
                    />
                    <input
                        type="text"
                        placeholder="Instagram link"
                        value={instaLink}
                        onChange={(e) => setInstagramLink(e.target.value)}
                    />
                </div>

                <button
                    className="profile-btn back-btn"
                    onClick={() => navigate("/profile")}
                >
                    Back
                </button>

                <button
                    className="profile-btn update-btn"
                    onClick={handleUpdate}
                    disabled={loading}
                >
                    {loading ? "Updating..." : "Update Profile"}
                </button>
            </div>
        </div>
    );
};

export default ProfileEdit;
